import React,{useEffect,useState} from 'react';

import '../Dropdown/DropdownSlider.css'


export default function DropdownSlider(props){
    const [imgs] = useState(props.imgs)
    
    const [imgIndex,setImgIndex]=useState(0);
    useEffect(()=>{
        const slide = setInterval(() => {
            setImgIndex(imgIndex + 1)
        }, 1500);

        return() => {
            clearInterval(slide)
        }
    }, [imgIndex])

    if (imgIndex >= imgs.length) {
        setImgIndex(0)
    }



return(
    <div className='dropdownslider'>
        <div className='dropdownslider-container' style={{width: '450px', height: '300px'}}>
            {
                imgs.map((item,index)=>{
                    return(
                        <img
                            key={index}
                            alt=''
                            src={item}
                            style={{transform: `translateX(${props.width }px`}}
                            width='100%' height='100%'
                            >
                         </img>
                    )
                })
            }
            
        </div>
        <div className='dropdownslider-dot'>
            {
                imgs.map((item,index)=>{
                    return(
                        <div
                        key={index}
                        className={index===imgIndex ? 'active-dot':''}
                        >
                          <i class="fas fa-circle  icon"></i>
                        </div>
                    )
                })
            }

        </div>

    </div>
)
}
