import React,{useEffect,useState} from 'react';
import{withRouter} from 'react-router-dom';
import '../Dropdown/DropdownSlider.css';
import {bannerImgs} from '../Dropdown/DropdownListItem';
 function Banner(props) {

    const [imgIndex, setImgIndex] = useState(0);
   

    useEffect(()=>{
        const slide = setInterval(() => {
            setImgIndex(imgIndex + 1)
        }, 1500);

        return() => {
            clearInterval(slide)
        }
    }, [imgIndex])

    if (imgIndex >= bannerImgs.length) {
        setImgIndex(0)
    }

    return(
        <div className="DropdownSlider">
            <div className="dropdownslider-container " >
                {
                    bannerImgs.map((item, index) => {
                        return (
                            <img 
                                key={index}
                                alt=""
                                style={{transform: `translateX(-${props.width * imgIndex}px`}}
                                src={item} width='100%' height='100%'></img>
                        )
                    })
                }
            </div>
            <div className="dropdownslider-dot flex-center">
                {
                    imgs.map((item, index) => {
                        return (
                            <div 
                                key={index}
                                className={index === imgIndex ? "active-dot" : ""}
                            >
                               <i class="fas fa-circle  icon"></i>
                            </div>
                        )
                    })
                }
            </div>
        </div>
    )
}
export default withRouter(Banner);
