import React,{useState} from 'react'
import {
     Link
  } from "react-router-dom";
import './Navbar.css';
 function Navbar(){
     const[click,setClick]= useState(false);
     const handleClick=()=>setClick(!click);
     const closeMobileMenu=()=>setClick(false);
    return (
        <>
            <nav className="Navbar">
               <Link to="/" className="nav-logo">MICHAEL</Link>
               <div className="menu-icon" onClick={handleClick}>
                  <i className={click ? 'fas fa-times': 'fas fa-bars' }>
                  </i>
               </div>           
               <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                  <li className='nav-item'>
                      <Link to='/' className='nav-link' onClick={closeMobileMenu}>Men</Link>
                  </li>
                  <li className='nav-item'>
                      <Link to='/' className='nav-link' onClick={closeMobileMenu}>Women</Link>
                  </li>
                  <li className='nav-item'>
                      <Link to='/' className='nav-link' onClick={closeMobileMenu}>Kids</Link>
                  </li>
                  <li className='nav-item'>
                      <Link to='/' className='nav-link' onClick={closeMobileMenu}>Sale</Link>
                  </li>
                  <li className='nav-item'>
                      <Link to='/' className='nav-link' onClick={closeMobileMenu}>About</Link>
                  </li>
                 
               </ul>
              
            </nav>
        </>
    ) 
 }
 export default Navbar;
