import React from 'react';
import MenImg1 from '../../Assets/men-dropdown.jpg';
import MenImg2 from '../../Assets/men-dropdown2.jpg';
import MenImg3 from '../../Assets/men-dropdown3.jpg';
import { faCircle } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';


export default function DropdownSlider(){
    const imgs=[MenImg1,MenImg2,MenImg3]
return(
    <div className='dropDownSlider'>
        <div className='dropDown-container'>
            {
                imgs.map((item,index)=>{
                    return(
                        <img
                         key={index}
                         src={item}
                         style={{transform: `translateX(-${props.width * imgIndex}px`}}
                         width='100%' height='100%'

                        />
                    )
                })
            }
            
        </div>
        <div className='dropDown-dot'>
            {
                imgs.map((item,index)=>{
                    return(
                        <div
                        key={index}
                        >
                            <FontAwesomeIcon icon={faCircle} className='icon'/>
                        </div>
                    )
                })
            }

        </div>

    </div>
)
}
