                                <div className='productquickview-tag-item new'>
                                   NEW
                               </div>
                            }
                        </div>
                        <div className='productquickview-slider-img'>
                            <Slider/>

                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
export default withRouter( ProductQuickView);
