import React,{useState} from 'react'
import MenuItems from './MenuItems'
import '.Dropdown.css'
export default function DropDown(){
 return(
     <>
     <ul
        onClick={handleClick}
        className={click ? 'dropdown-menu clicked' : 'dropdown-menu'}
      >
        {MenuItems.Men.map((item, index) => {
          return (
            <li key={index}>
              <Link
                
                to={item.path}
                onClick={() => setClick(false)}
              >
                {item.title}
              </Link>
            </li>
          );
        })}
      </ul>
     </>
 )
}
