import React,{useEffect,useState} from 'react';


function News(props){
    const news=props.news;
    var date=new Date(news.newTime);
    return(
        <div className='news'>
            <div className='news-img'>
                <img src={news.newImg} alt=''/>
            </div>
            <div news-info>
                <div news-date>{date.getDate()}.{date.getMonth()-1}.{date.getFullYear()}</div>
            </div>
        </div>
    )
}
export default News;
