import React,{useEffect,useState} from 'react';
import MenImg1 from '../../Assets/men-dropdown.jpg';
import MenImg2 from '../../Assets/men-dropdown2.jpg';
import MenImg3 from '../../Assets/men-dropdown3.jpg';
import '../Dropdown/DropdownSlider.css'


export default function DropdownSlider(){
    const imgs=[MenImg1,MenImg2,MenImg3];
    const [imgIndex,setImgIndex]=useState(0);
    useEffect(()=>{
        const slide = setInterval(() => {
            setImgIndex(imgIndex + 1)
        }, 1500);

        return() => {
            clearInterval(slide)
        }
    }, [imgIndex])

    if (imgIndex >= imgs.length) {
        setImgIndex(0)
    }



return(
    <div className='dropdownslider'>
        <div className='dropdownslider-container' style={{width: '450px', height: '300px'}}>
            {
                imgs.map((item,index)=>{
                    return(
                        <img
                         key={index}
                         alt=''
                         src={item}
                         style={{transform: `translateX(-${450*imgIndex}px)`}}
                         width='100%' height='100%'

                        />
                    )
                })
            }
            
        </div>
        <div className='dropdownslider-dot'>
            {
                imgs.map((item,index)=>{
                    return(
                        <div
                        key={index}
                        className={index===imgIndex ? 'active-dot':''}
                        >
                          <i class="fas fa-circle active-dot icon"></i>
                        </div>
                    )
                })
            }

        </div>

    </div>
)
}
