import React,{useContext} from 'react';
import './Cart.css';
import {CartContext} from '../../Context/Cart'
import Axios from 'axios';

 function WishList(props){
     const {wishListItems,addToCart,removeWishLish}=useContext(CartContext);
     const cartClick = (event) => {
        const id = event.target.id
        Axios.get(`http://localhost:3001/products/${id}`)
            .then(res => {
                res.data.map((item,index)=>{
                    return(addToCart(item))
                    
                })
            }
        )
    }

     return(
         <div className='wish-list'>
             <div className='cartitems-list'>
                 {wishListItems.length===0&&
                 <div className='no-product'> No products in the wishlists</div>
                 }
              
                 {
                    wishListItems.map((item,index)=>{
                        return(
                            <div className='cartitem-product' key={index}>
                                <div className='cartitem-image'>
                                    <img src={item.productImg[0]}
                                    width='80px'height='100%' 
                                    alt=''                                  
                                    />
                                </div>
                                <div className='cartitem-name'>{item.productName}</div>

                                <div className='cartitem-price price'>
                                    {item.productFinalPrice.toString()
                                    .replace(/\B(?=(\d{3})+(?!\d))/g, ".")} Ä‘
                                </div>
                                <div className='cartitem-add'
                                     id={item._id}
                                     onClick={(event)=>{cartClick(event)
                                        removeWishLish(event)
                                    }}
                                >
                                    <i className="fa fa-cart-plus" style={{pointerEvents:'none'}} />
                                    <div className='add'>Add to cart</div>
                                </div>
                                <div className='cartitem-remove'>
                                    <i className="fa fa-times" 
                                    id={item._id}
                                    onClick={removeWishLish}
                                    />
                                </div>
                            </div>
                        )
                    })
                 }
             </div>
         </div>
     )
 }
 export default WishList;
